import React, { useState, useEffect, useRef   } from "react";
import { useDispatch, useSelector } from "react-redux";
import { FilterMatchMode, FilterOperator } from 'primereact/api';
import { toggleLoadingStateAction,setSelectedEmployeeStateAction,setShowEmpDetailsstateAction } from "../store/reducers";
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import { InputText } from 'primereact/inputtext';
import { IconField } from 'primereact/iconfield';
import { InputIcon } from 'primereact/inputicon';
import EmployeeDetails from './EmployeeDetails'
import { OverlayPanel } from 'primereact/overlaypanel';
import { Dialog } from 'primereact/dialog';
import { Button } from 'primereact/button';
import './EmployeeSummary.css';


const EmployeeSummary = () => {
// const op = useRef(null);  
  const dispatch = useDispatch();
  const companyInfoState = useSelector ( (state) => state.companyInfoState)
  const [filters, setFilters] = useState({
    global: { value: null, matchMode: FilterMatchMode.CONTAINS },
    firstName: { value: null, matchMode: FilterMatchMode.CONTAINS },

});
const [globalFilterValue, setGlobalFilterValue] = useState('');

const onGlobalFilterChange = (e) => {
    const value = e.target.value;
    let _filters = { ...filters };

    _filters['global'].value = value;

    setFilters(_filters);
    setGlobalFilterValue(value);
};


const renderHeader = () => {
    return (
        <div className="flex justify-content-end">
            <div></div>
            <IconField iconPosition="left">
                <InputIcon className="pi pi-search" />
                <InputText value={globalFilterValue} onChange={onGlobalFilterChange} placeholder="Keyword Search" />
            </IconField>
        </div>
    );
};
const header = renderHeader();
  return (
    <>
    {/* <div>{companyInfoState.companyInfo.companyName}</div>
    <button onClick={ (e)=> {
        dispatch(toggleLoadingStateAction())
    }}>toggle</button>
    <div> loading: {companyInfoState.loading ? 'loading': 'not loading'}</div>
    <div>
        {
            companyInfoState.companyInfo.companyName
        }

    </div> */}
    <div>
        <div>
            <div>{companyInfoState.companyInfo.companyName}</div>
            <div>{companyInfoState.companyInfo.companyMotto}</div>
        </div>

    <div></div>

<div className="card">
    <DataTable value={companyInfoState.employees} paginator rows={5} dataKey="id" filters={filters}  header={header} 
    onRowClick={(e)=>{
    // op.current.toggle(e)
    console.log("event",e)
    dispatch(setSelectedEmployeeStateAction(e.data))
    dispatch(setShowEmpDetailsstateAction(true))


}}
>
    <Column field="id" header="ID"></Column>
    <Column field="firstName" header="Name" ></Column>
    <Column field="contactNo" header="Contact No"></Column>
    <Column field="address" header="Address"></Column>
    {/* <Column field="avatar" header="Address"></Column> */}
    </DataTable>
    </div>    
    </div>

    <div className="card flex justify-content-center">
    {/* <OverlayPanel ref={op} showCloseIcon> */}
    <Dialog header="Header" visible={companyInfoState.showEmpDetails} modal={false} style={{ width: '50vw' }} >
    <EmployeeDetails></EmployeeDetails>
    </Dialog>

    {/* </OverlayPanel> */}
    </div>

    </>
  );
};

export default EmployeeSummary ;
